SELECT * FROM ( SELECT ROWNUM KNUM , T.RNUM , T.BRD_ID , T.BRD_TITLE , T.BRD_WRTR , T.BRD_WRT_DT , T.BRD_MDFY_DT , T.BRD_HIT FROM ( SELECT ROW_NUMBER() OVER(ORDER BY BRD_ID) RNUM , BRD_ID , BRD_TITLE , (SELECT EMP_NAME FROM EMPLOYEE WHERE EMP_ID = BRD_WRTR) BRD_WRTR , BRD_WRT_DT , BRD_MDFY_DT , BRD_HIT FROM BOARD WHERE BRD_TYPE_ID = 'G103' AND BRD_DEL_DT IS NULL AND 1 = 1
AND ( BRD_TITLE LIKE '%'||#{keyWord}||'%' OR BRD_WRTR LIKE '%'||#{keyWord}||'%')
ORDER BY BRD_ID DESC ) T ) K WHERE K.KNUM BETWEEN #{start} AND #{end}

