insert.foreach:
INTO ATTACH (
 ATCH_ID
,ATCH_REL_ID
,ATCH_PATH
,ATCH_NAME
,ATCH_OLDNAME
,ATCH_EXTNS
,ATCH_WRT_DATE
,ATCH_SIZE
,ATCH_WRTR
) VALUES (
(SELECT 'ATTA'||LPAD(NVL(MAX(SUBSTR(ATCH_ID,5,5)),0)+1+#{index},5,'0') FROM ATTACH)
,#{item.atchRelId}
,#{item.atchPath}
,#{item.atchName}
,#{item.atchOldname}
,#{item.atchExtns}
,SYSDATE
,#{item.atchSize}
,#{item.atchWrtr}
)
