mapper.update:
UPDATE ATNDN_DLY
SET
ATNDN_END_DT = #{atndnEndDt}
,ATNDN_TYPE_ID = #{atndnTypeId}

WHERE
ATNDN_DLY_ID = #{atndnDlyId}
AND EMP_ID = #{empId}

mapper.select:
SELECT *
FROM ATNDN_DLY
WHERE to_char(ATNDN_DLY_DT,
'YYYY-MM-DD') = #{atndnDlyDt}
and emp_id = #{empId}

mapper.update:
UPDATE ATNDN_DLY
SET
ATNDN_DLY_RSN = #{atndnDlyRsn}
WHERE ATNDN_DLY_ID = #{atndnDlyId}
