mapper.select:
SELECT ATRZ_LINE_ID, ATRZ_LINE_LEVEL, ATRZ_APRV_ID, ATRZ_NAME, POSITION_NAME, DEPT_NAME
  FROM ATRZ_LINE_DETAIL
 WHERE ATRZ_LINE_ID LIKE #{atrzLineId}

SELECT T.RNUM , T.ATRZ_STATE , T.DOC_ID , T.DOC_TITLE , T.DOC_WRT_DT , T.DOC_WRTR , T.DOC_WRTR_NAME FROM ( SELECT
ROW_NUMBER() OVER(ORDER BY DOC_MDFY_DT DESC, DECODE(A.DOC_STATE,'미결재',1,'진행중',2,'반려',3,'결재완료',4) ASC, A.DOC_WRT_DT DESC) RNUM ,CASE WHEN A.DOC_STATE LIKE '대기중' THEN '미결재' WHEN A.DOC_STATE LIKE '심사중' THEN CASE WHEN B.ATRZ_TYPE_ID LIKE '대기중' THEN '미결재' ELSE '진행중' END WHEN A.DOC_STATE LIKE '결재완료' THEN '결재완료' WHEN A.DOC_STATE LIKE '반려' THEN '반려' END AS ATRZ_STATE
ROW_NUMBER() OVER(ORDER BY DOC_MDFY_DT DESC, DECODE(A.DOC_STATE,'진행중',1,'대기중',2,'반려',3,'결재완료',4) ASC, A.DOC_WRT_DT DESC) RNUM ,CASE A.DOC_STATE WHEN '대기중' THEN '대기중' WHEN '심사중' THEN '진행중' WHEN '결재완료' THEN '결재완료' WHEN '반려' THEN '반려' END AS ATRZ_STATE
, A.DOC_ID , A.DOC_TITLE , A.DOC_WRT_DT , A.DOC_WRTR , A.DOC_WRTR_NAME FROM ATRZ_DOC A, ATRZ B WHERE 1=1 AND A.DOC_ID = B.DOC_ID
AND A.DOC_STATE NOT LIKE '임시저장' AND B.ATRZ_APRV_ID LIKE #{userId}
AND A.ATRZ_LEVEL >= B.ATRZ_LEVEL
AND DOC_WRTR LIKE #{userId} AND DOC_STATE NOT LIKE '임시저장'
) T WHERE T.RNUM BETWEEN #{start} AND #{end}
ORDER BY DECODE(T.ATRZ_STATE,'미결재',1) ASC

mapper.update:
UPDATE EMPLOYEE
   SET EMP_SIGN = #{empSign}
 WHERE EMP_ID = #{empId}

mapper.select:
SELECT ATRZ_ID, DOC_ID, ATRZ_LEVEL, ATRZ_TYPE_ID, ATRZ_CMT, 
	   ATRZ_WRT_DT, ATRZ_APRV_DT, ATRZ_APRV_ID, ATRZ_SIGN
  FROM ATRZ
 WHERE DOC_ID = #{docId}
   AND ATRZ_APRV_ID = #{atrzAprvId}

mapper.update:
UPDATE ATRZ
   SET ATRZ_TYPE_ID = #{atrzTypeId},
       ATRZ_SIGN = #{atrzSign},
       ATRZ_APRV_DT = SYSDATE
 WHERE DOC_ID = #{docId}
   AND ATRZ_APRV_ID = #{atrzAprvId}
